use std::vec::Vec;

fn main() {
    // let input = String::from("mjqjpqmgbljsphdztnvjfqwrcgsmlb");
    let input = String::from("");
    let size: usize = 14;

    for i in 0..input.len()-(size-1) {
        let part = &input[i..i+size];
        println!("handling {} - {} : {}", i, i + size, part);
        let mut part_chars: Vec<char> = part.chars().collect();
        part_chars.sort();
        part_chars.dedup();
        if part_chars.len() == size {
            println!("first marker at {}", i + size);
            break;
        }
    }

}